import React from 'react';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

class Blog extends React.Component {

    state = {
        shows: [],
    }

    async componentDidMount() {
        const respons = await fetch('https://api.tvmaze.com/search/shows?q=batman');
        const resData = await respons.json();
        this.setState({shows: resData});
    }

    render(){
        console.log('this.state.shows', this.state.shows);

        return(
            <div>
                <h3>Batman TV Shows</h3>
                <ul> 
                    { this.state.shows.map(show => {
                        return(
                        <li key={show.show.id}>
                            <Link href="#">
                                <p> { show.show.name }  </p>
                            </Link> 
                        </li>
                        )

                    }) }
                </ul>  
                <ul>
                    {
                        this.state.shows.map(showPost => {
                            return(
                            <li>
                                <a href="#"> { showPost.show.id } </a>
                            </li>
                            )

                        //   {console.log('test id', showPost.show.id )}

                        })
                    }
                </ul>

            </div>
    )}
}


export default Blog;
