import React from 'react';



class Body extends React.Component {
    state ={
        shows: [],
    }

    async componentDidMount() {
        const respons = await fetch('https://api.tvmaze.com/search/shows?q=batman');
        const resData = await respons.json();
        this.setState({shows: resData});
    }
    getData(){
        this.state.shows.map(person => {
            return(
                <ul>
                    <li>
                            <p> { person.show.name } </p>
                            {/* <img src={person.show.image.original} /> */}
                    </li>
                </ul>
            )  
        })
    }
    render(){
        console.log('this.state.shows', this.state.shows);

        const layoutStyle = {
            margin: 20,
            padding: 20,
            border: '1px solid #DDD',
            display: "flex",
            width: "90%",
            backgroud: "#ccc"
          };
    return(
       <div>
           <h3 onClick={this.getData}>My Power</h3>
           <ul>
               {
                   this.state.shows.map(person => {
                    return(
                        <ul className="about-list" style={layoutStyle}>
                            <li>
                                <div className="about-person">
                                    <h3> { person.show.name } </h3>
                                    <p> { person.show.summary } </p>
                                    <img src={person.show.image.original} />
                                </div>     
                            </li>
                        </ul>
                    )  
                })
               }
           </ul>
        </div>
    )

}
}

export default Body;
